// 功能 1: 查詢所有術語
async function fetchAllTerms() {
  const out = document.getElementById('out_terms');
  out.textContent = '正在請求資料...';

  try {
    const resp = await fetch(`${BASE_URL}/terms`, {
      mode: 'cors',
      headers: {
        'Accept': 'application/json'
      }
    });
    let info = `HTTP ${resp.status} ${resp.statusText}\n\n`;

    if (!resp.ok) {
      const text = await resp.text();
      out.textContent = info + text;
      return;
    }

    const data = await resp.json();
    out.textContent = info + JSON.stringify(data, null, 2);
  } catch (err) {
    console.error('Fetch error:', err);
    out.textContent = 
      '❌ 錯誤: ' + (err && err.message ? err.message : String(err)) +
      '\n\n📝 詳細資訊:' +
      '\n- 請求網址: ' + BASE_URL + '/terms' +
      '\n- 錯誤類型: ' + err.name +
      '\n\n🔍 常見原因:' +
      '\n1) 後端伺服器未啟動或無法連線' +
      '\n2) 伺服器未設定 CORS (Access-Control-Allow-Origin)' +
      '\n3) 需要在校園網路或 VPN 內訪問' +
      '\n4) 網路連線問題' +
      '\n\n💡 請確認:' +
      '\n- 後端伺服器是否正在運行?' +
      '\n- 您的瀏覽器控制台 (F12) 有什麼錯誤訊息?';
  }
}

