<html lang="zh-TW">
<head>
  <meta charset="UTF-8">
  <title>簡單 API 測試</title>
  <style>
    body { font-family: monospace; padding: 20px; background: #1e1e1e; color: #fff; }
    button { padding: 10px 20px; font-size: 16px; margin: 10px; cursor: pointer; }
    pre { background: #000; padding: 15px; border-radius: 5px; overflow: auto; }
    .info { color: #4fc3f7; }
    .success { color: #81c784; }
    .error { color: #e57373; }
  </style>
</head>
<body>
  <h1>🧪 API 連線測試</h1>

  <div class="info">
    <p>📍 當前位置: <span id="location">載入中...</span></p>
    <p>🌐 協定: <span id="protocol"></span></p>
    <p>⏰ 測試時間: <span id="time"></span></p>
  </div>

  <button onclick="testAPI()">🚀 測試 API</button>
  <button onclick="testWithTimeout()">⏱️ 測試 (5秒超時)</button>

  <h2>結果:</h2>
  <pre id="output">按下按鈕開始測試...</pre>

  <script>
    // 顯示環境資訊
    document.getElementById('location').textContent = window.location.href;
    document.getElementById('protocol').textContent = window.location.protocol;
    document.getElementById('time').textContent = new Date().toLocaleString('zh-TW');

    async function testAPI() {
      const output = document.getElementById('output');
      const url = 'https://mil.psy.ntu.edu.tw:5000/terms';

      output.className = 'info';
      output.textContent = `⏳ 正在連接: ${url}\n開始時間: ${new Date().toLocaleTimeString()}`;

      const startTime = Date.now();

      try {
        const response = await fetch(url, {
          method: 'GET',
          mode: 'cors'
        });

        const endTime = Date.now();
        const duration = endTime - startTime;

        output.className = 'success';
        output.textContent = `✅ 成功!\n`;
        output.textContent += `⏱️ 回應時間: ${duration}ms\n`;
        output.textContent += `📊 狀態: ${response.status} ${response.statusText}\n\n`;

        const data = await response.json();
        output.textContent += `📦 資料:\n${JSON.stringify(data, null, 2)}`;

      } catch (error) {
        const endTime = Date.now();
        const duration = endTime - startTime;

        output.className = 'error';
        output.textContent = `❌ 失敗!\n`;
        output.textContent += `⏱️ 耗時: ${duration}ms (${(duration/1000).toFixed(1)}秒)\n`;
        output.textContent += `🔴 錯誤: ${error.name}\n`;
        output.textContent += `💬 訊息: ${error.message}\n\n`;
        output.textContent += `📝 完整錯誤:\n${error.stack || error}`;
      }
    }

    async function testWithTimeout() {
      const output = document.getElementById('output');
      const url = 'https://mil.psy.ntu.edu.tw:5000/terms';

      output.className = 'info';
      output.textContent = `⏳ 正在連接 (5秒超時): ${url}\n`;

      const startTime = Date.now();

      try {
        const controller = new AbortController();
        const timeoutId = setTimeout(() => controller.abort(), 5000);

        const response = await fetch(url, {
          method: 'GET',
          mode: 'cors',
          signal: controller.signal
        });

        clearTimeout(timeoutId);
        const endTime = Date.now();

        output.className = 'success';
        output.textContent = `✅ 成功! 回應時間: ${endTime - startTime}ms\n`;

        const data = await response.json();
        output.textContent += JSON.stringify(data, null, 2);

      } catch (error) {
        const endTime = Date.now();

        output.className = 'error';
        output.textContent = `❌ 失敗! 耗時: ${endTime - startTime}ms\n`;

        if (error.name === 'AbortError') {
          output.textContent += `⏱️ 連線超時 (超過 5 秒)\n`;
          output.textContent += `\n💡 這表示:\n`;
          output.textContent += `  • 伺服器無回應\n`;
          output.textContent += `  • 可能伺服器未啟動\n`;
          output.textContent += `  • 或防火牆封鎖連線\n`;
        } else {
          output.textContent += `🔴 ${error.name}: ${error.message}\n`;
        }
      }
    }
  </script>
</body>
</html>
